const Cards = () => {
  return (
    <div>
      <Swiper
        className="container card__container"
        modules={[Pagination, Scrollbar]}
        spaceBetween={40}
        slidesPerView={3}
        pagination={{ clickable: true }}
        breakpoints={{
          320: {
            slidesPerView: 1,
            spaceBetween: 20,
          },
          375: {
            slidesPerView: 1,
            spaceBetween: 20,
          },
          425: {
            slidesPerView: 1,
            spaceBetween: 20,
          },
          768: {
            slidesPerView: 2,
            spaceBetween: 40,
          },
          1024: {
            slidesPerView: 3,
            spaceBetween: 50,
          },
        }}
      >
        {skillCards.map(({ logo, name, id, content }) => {
          return (
            <SwiperSlide key={id} className="skill__card">
              <div className="skill__logo">
                <div>{logo}</div>
              </div>
              <h3 className="skill__name">{name}</h3>
              <p className="skill__content">{content}</p>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </div>
  );
};

export default Cards;
